import styled from "styled-components";


export const Container = styled.section`

  //b4b4b4

  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;



  div:nth-child(1) {
    display: flex;
    justify-content: center;
    align-items: center;

    margin-bottom: 3rem;


    h1 {
      color: #b4b4b4;
      font-size: 2rem;
    }

    img {
      width: 70px;
      margin-right: 0.5rem;
    }
  }

  div:nth-child(2) {
    width: 12rem;
    height: 12rem;
    background: #fff;
    display: flex;
    justify-content: center;
    align-items: center;

    border-radius: 0.5rem;
    box-shadow: 0 0 20px rgb(0, 0, 0, 0.15);

    cursor: pointer;

    transition: box-shadow 0.2s;

    &:hover {
      box-shadow: 0 0 25px rgb(0, 0, 0, 0.2);
      animation-name: folder-moviment;
    }

    img {
      opacity: 0.5;
      animation-duration: 0.8s;
      animation-iteration-count: infinite;
      transition: all 0.5s;
    }

    @keyframes folder-moviment {
      0% { margin-top: 0; }
      50% { margin-top: -10px; }
      100% { margin-top: 0px; }
    }
  }

`;
