/> */

export function Categories() {
  const [isOpenCategoryModal, setIsOpenCategoryModal] = useState<boolean>(false);

  const fakeData = [
    {
      bgColor: "blue",
      textColor: "black",
      content: "segundo card teste",
      emojiID: "cart"
    },
    {
      bgColor: "black",
      textColor: "white",
      content: "Teste de cartão dinamico kkk",
      emojiID: "croissant"
    },
    {
      bgColor: "pink",
      textColor: "red",
      content: "terceiro teste de texto longo para evitar possiveis erros dos uusuarios leigos e dementes",
      emojiID: "croissant"
    }
  ]

  return (
    <>
      {
        fakeData.length > 0 
        ? <>
          <CategoriesHeader>
            <div>
              <h1>Categorias</h1>

              <img src={OptionsStruct} alt="Estrutura das opções" />
            </div>

            <hr />
          </CategoriesHeader>

          <CategoriesContent>
            <CategoryCard data={{
              bgColor: "black",
              textColor: "white",
              content: "Teste de cartão dinamico kkk",
              emojiID: "croissant"
            }} />
          </CategoriesContent>        
        </>
        : <div>Não tem</div>
      }
    </>
  );
}
