import styled from "styled-components";


export const teste = styled.div``;

export const CategoryModalForm = styled.section`

  .modal-title-header {
    display: flex;
    align-items: center;
    margin-bottom: 1.4rem;

    img {
      width: 40px;
      height: 40px;
      margin-right: 1rem;
    }

    h1 {
      font-size: 1.6rem;
    }
  }

  .modal-title-input {
    margin-bottom: 1.4rem;

    input[type="text"] {
      border: solid 1px #b9b9b9; 
      width: 100%;
      height: 3rem;
      padding-left: 0.75rem;
      font-size: 1rem;
    }
  }

  .modal-configs-inputs {
    > div:nth-child(1) {
      display: flex;
      justify-content: space-between;

      > div:nth-child(2) {
        width: 1px;
        background: rgb(0, 0, 0, 0.3);
      }
    }

    h4 {
      margin-bottom: 1rem;
    }
  }

  .finish-new-category {
    padding-top: 2rem;
    button {
      border: none;
      background-color: #363676;
      color: white;
      padding: 1rem 1.5rem;
      border-radius: 0.25rem;
    }
  }


`;

export const RoundColorPicker = styled.div`

  display: flex;
  align-items: center;

  & + div {
    margin-top: 0.5rem;
  }

  div:nth-child(1) {
    box-shadow: 1px 1px 10px rgb(0, 0, 0, 0.5);
    border-radius: 50%;

    width: 2.5rem;
    height: 2.5rem;

    display: flex;
    justify-content: center;
    align-items: center;

    margin-right: 0.5rem;
    overflow: hidden;

    input[type="color"] {
      border: none;
      background-color: transparent;
      width: 150%;
      height: 150%;
      margin: -25%;
    }
  }

`;
