import styled from "styled-components";


export const Container = styled.div`
  width: max-content;
  max-width: 20rem;
  height: max-content;
  max-height: 15rem;

  min-width: 4rem;
  min-height: 4rem;

  border: solid 1px red;
  border-radius: 0.25rem;
  background-color: indigo;
  color: white;
  font-size: 2rem;
  padding: 0.5rem;

`;
