import styled from "styled-components";


type HeaderProps = {
  bgColor: string;
  textColor: string;
}

export const Header = styled.header<HeaderProps>`

  width: 100%;
  min-height: 15rem;
  background-color: ${props => props.bgColor ? props.bgColor : "#0f3f86"};
  display: flex;
  justify-content: space-between;

  padding: 1rem;

  > div:nth-child(1) {
    align-self: self-end;

    > a div:nth-child(1) {

      display: flex;
      align-items: end;

      img {
        margin-right: 1rem;
      }

      h2 {
        color: white;  
      }
    }
  }

  > div:nth-child(2) {
    align-items: end;
    display: flex;

    > div:nth-child(1) {
      background-color: rgb(255, 255, 255, .1);
      align-items: end;
      display: flex;

      padding: 0.5rem;
      border-radius: 0.5rem;

      h1 {
        margin-left: 1rem;
        color: ${props => props.textColor ? props.textColor : "#fff"};
      }
    }
  }

`;


export const Section = styled.section`


`;
