import styled from "styled-components";


type HeaderProps = {
  bgColor: string;
  textColor: string;
}

export const Header = styled.header<HeaderProps>`

  width: 100%;
  min-height: 15rem;
  background-color: ${props => props.bgColor ? props.bgColor : "#0f3f86"};
  display: flex;
  justify-content: space-between;

  padding: 1rem;

  > div:nth-child(1) {
    align-self: self-end;

    > a div:nth-child(1) {

      display: flex;
      align-items: end;

      img {
        margin-right: 1rem;
      }

      h2 {
        color: white;  
      }
    }
  }

  > div:nth-child(2) {
    align-items: end;
    display: flex;

    > div:nth-child(1) {
      background-color: rgb(255, 255, 255, .1);
      align-items: end;
      display: flex;

      padding: 0.5rem;
      border-radius: 0.5rem;

      h1 {
        margin-left: 1rem;
        color: ${props => props.textColor ? props.textColor : "#fff"};
      }
    }
  }

`;


export const Section = styled.section`
  border: solid 1px red;
  padding: 2rem;
`;

export const Container = styled.main`


`;

export const CreateNewTask = styled.div`

  display: flex;
  font-weight: 700;
  font-size: 1.5rem;

  padding: 1rem;
  background: #13132a85;

  > div:nth-child(1) {
    border-radius: 50%;
    background-color: white;
    width: 2rem;
    height: 2rem;

    display: flex;
    align-items: center;
    justify-content: center;

    margin-right: 0.5rem;
  }

  > div:nth-child(2) {
    align-self: end;
    color: white;
  }
`;

export const TaskContainer = styled.div`


`;
