import styled from "styled-components";

export const Header = styled.header`
  > div:nth-child(1) {
    display: flex;
    justify-content: space-between;
    align-items: center;

    img {
      width: 3rem;
      cursor: pointer;
    }

    div {
      display: flex;
      align-items: center;
    }
  }

  hr {
    margin: 0.5rem 0;
  }
`;

export const Section = styled.section`


`;

export const Task = styled.div`

`;
