import { useEffect, useState } from "react";
import { Section, Header, Task, NotHaveTasks } from "./styles";
import { useParams, Link } from "react-router-dom";
import { Emoji } from "emoji-mart";
import { CategoryInfo } from "../../hooks/useCategories";

import ArrowBack from "../../assets/arrowBack.svg";
import TaskIcon from "../../assets/task.svg";
import Checked from "../../assets/checkbox.svg";

type Task = {
  content: string;
  finish: boolean;
  categoryID: string;
}

export function Tasks() {
  const [category, setCategory] = useState<CategoryInfo>({
    bgColor: "white",
    categoryID: "loadings",
    content: "loading...",
    emojiID: "warning",
    textColor: "black",
  });
  const [allTasks, setAllTasks] = useState<Task[]>([]);
  const [newTask, setNewTask] = useState<Task>({
    content: "",
    finish: false,
    categoryID: "",
  });

  const params = useParams() || "";

  function getLocalTasks(categoryID: string) {
    let data = JSON.parse(localStorage.getItem("@to-do-list/categories") || "{}")

    for (var c = 0; c < data.length; c++) {
      if (data[c].categoryID === categoryID) {
        return data[c];
      }
    }

    return {}; // ID DE URL ERRADO
  }

  useEffect(() => {
    const tasks = getLocalTasks(String(params.categoryID)); // Title of category

    setCategory(tasks);
  }, []);

  function newLocalTask() {
    let localData = JSON.parse(localStorage.getItem("@to-do-list/tasks") || "[]")

    setAllTasks([
      ...allTasks, 
      { 
        content: newTask.content, 
        categoryID: params.categoryID || "", 
        finish: false 
      }
    ])
  }

  function handleNewTask() {
  }

  return (
    <>
      <Header>
        <div>
          <Link to={"/categories"}>
            <img src={ArrowBack} alt="Voltar"/>
          </Link>

          <div>
            <Emoji emoji={category.emojiID} set="facebook" size={40} />

            <h2>{category.content}</h2>
          </div>
        </div>

        <hr />
      </Header>

      <Section>
        <div>
          <label onClick={handleNewTask} htmlFor="new-task" style={
              newTask.content.length > 0
              ? { backgroundColor: "green", color: "#fff" }
              : {}
          }>
            <i className="fa-solid fa-plus"></i>
          </label>

          <div>
            <input 
              value={newTask.content}
              onChange={(e) => setNewTask({ ...newTask, content: e.target.value })}
              id={"new-task"} 
              type={"text"} 
              placeholder={"Adicionar nova tarefa"} 
            />
          </div>
        </div>

        <Task>
          <div>
            <img src={Checked} alt="checar" />
            <div>Conteúdo aqui here o quem dirifjdf jjfjdf dfdfjhehr</div>
          </div>


          <div>
            <i className="fa-solid fa-pen-to-square"></i>
            <i className="fa-solid fa-trash-can"></i>
          </div>
        </Task>

        <Task>
          <div>
            <img src={Checked} alt="checar" />
            <div>Conteúdo aqui here o quem dirifjdf jjfjdf dfdfjhehr</div>
          </div>


          <div>
            <i className="fa-solid fa-pen-to-square"></i>
            <i className="fa-solid fa-trash-can"></i>
          </div>
        </Task>

        <Task>
          <div>
            <img src={Checked} alt="checar" />
            <div>Conteúdo aqui here o quem dirifjdf jjfjdf dfdfjhehr</div>
          </div>


          <div>
            <i className="fa-solid fa-pen-to-square"></i>
            <i className="fa-solid fa-trash-can"></i>
          </div>
        </Task>

      <NotHaveTasks>
          <img src={TaskIcon} alt="Nenhuma task encontrada" />

          <h1>Nenhuma tarefa encontrada</h1>
        </NotHaveTasks>
      </Section>
    </>
  );
}
