import { useState, useEffect } from "react";
import { Emoji } from "emoji-mart";
import { Link, useParams } from "react-router-dom";

import { Header } from "./styles";
import ImgBack from "../../assets/back.svg";

export function Tasks() {
  const [category, setCategory] = useState({
    bgColor: "#0f3f86",
    categoryID: "loadings",
    content: "loading...",
    emojiID: "warning",
    textColor: "white",
  });

  const params = useParams();

  function getCategory(categoryID: string) {
    let data = JSON.parse(localStorage.getItem("@to-do-list/categories") || "[]")

    for (let c = 0; c < data.length; c++) {
      if (data[c].categoryID === categoryID) {
        return setCategory(data[c]);
      }
    }
  }

  useEffect(() => {
    getCategory(params.categoryID || "");
  }, [])

  return (
    <Header bgColor={category.bgColor}>
      <div>
        <Link to={"/categories"} >
          <div>
            <img src={ImgBack} alt="Voltar" />
            <h2 color={category.textColor}> Voltar</h2>
          </div>
        </Link>
      </div>

      <div>
        <div>
          <Emoji emoji={"heavy_plus_sign"} set='facebook' size={40} />  

          <h1>Lembretes</h1>
        </div>
      </div>
    </Header>
  );
}
