import ReactModal from "react-modal";
import { api } from "../../services/api";
import { useEffect, useState } from "react";
import { ToastContainer, toast } from "react-toastify";


import { Container, InputContainer, Button } from "./styles";
import ImgCloseModal from "../../assets/close.svg";

type RegisterProps = {
  isOpen: boolean;
  setIsOpen: (param: boolean) => void;
}

export function Register(props: RegisterProps) {
  const [register, setRegister] = useState({
    name: "",
    email: "",
    password: ""
  })

  useEffect(() => {
    async function load() {
      let response = await api.post("/login", { ola: "Mundo" })

      console.log("Response: ", response);
    }

    toast("Olaaaaa");

    load();
  }, [])


  return (
    <>
      <ToastContainer />

      <ReactModal
        isOpen={props.isOpen}
        className={"modal-content"}
        overlayClassName={"modal-overlay"}
        onRequestClose={() => props.setIsOpen(false)}
      >
        <img
          onClick={() => props.setIsOpen(false)}
          className="close-modal-icon"
          src={ImgCloseModal}
          alt="Fechar modal"
        />
        <Container>

          <InputContainer>
            <label htmlFor="name">Nome completo</label>
            <input 
              value={register.name}
              onChange={(e) => setRegister({ ...register, name: e.target.value })}
              id="name" 
              type="text" 
              placeholder="Seu nome completo"
            />
          </InputContainer>

          <InputContainer>
            <label>Email</label>
            <input 
              value={register.email}
              onChange={(e) => setRegister({ ...register, email: e.target.value })}
              id="email" 
              type="email" 
              placeholder="EndereÃ§o de email" 
            />
          </InputContainer>

          <InputContainer>
            <label>Senha</label>
            <input 
              value={register.password}
              onChange={(e) => setRegister({ ...register, password: e.target.value })}
              id="password" 
              type="password" 
              placeholder="Digite uma senha" 
            />
          </InputContainer>


          <InputContainer>
            <label>Digite a senha novamente</label>
            <input id="password" type="password" placeholder="Para confirmar, digite novamente sua senha" />
          </InputContainer>


          <Button>Cadastre-se</Button>

        </Container>
      </ReactModal>
    </>
  );
}
