import ReactModal from "react-modal";
import { api } from "../../services/api";
import { useEffect, useState } from "react";
import { ToastContainer, toast } from "react-toastify";


import { Container, InputContainer, Button } from "./styles";
import ImgCloseModal from "../../assets/close.svg";

type RegisterProps = {
  isOpen: boolean;
  setIsOpen: (param: boolean) => void;
}

export function Register(props: RegisterProps) {
  const [register, setRegister] = useState({
    name: "",
    email: "",
    password: "",
    password2: "",
  })

  async function Register() {
    if (register.name.length <= 2)
      toast.warn("Nome muito curto.");

    else if (register.email.indexOf("@") === -1)
      toast.warn("Email inválido.");

    else if (register.password.length < 6)
      toast.warn("A senha precisa ter no mínimo 6 digitos.");

    else if (register.password !== register.password2)
      toast.warn("As senhas não estão iguais.");

    else {

      const id = toast.loading("Please wait...")

      let {data} = await api.post("/register", { ...register });

      toast.update(id, {
        autoClose: 3000, 
        render: data.success ? "Registro finalizado com sucesso!" : "Ocorreu um erro ao registrar usuario :(", 
        type: data.success ? "success" : "error", 
        isLoading: false 
      });

      setTimeout(() => {
        // Redirect user
      }, 3000);
    }
  } 

  return (
    <>
      <ToastContainer />

      <ReactModal
        isOpen={props.isOpen}
        className={"modal-content"}
        overlayClassName={"modal-overlay"}
        onRequestClose={() => props.setIsOpen(false)}
      >
        <img
          onClick={() => props.setIsOpen(false)}
          className="close-modal-icon"
          src={ImgCloseModal}
          alt="Fechar modal"
        />
        <Container>

          <InputContainer>
            <label htmlFor="name">Nome completo</label>
            <input 
              value={register.name}
              onChange={(e) => setRegister({ ...register, name: e.target.value })}
              id="name" 
              type="text" 
              placeholder="Seu nome completo"
            />
          </InputContainer>

          <InputContainer>
            <label>Email</label>
            <input 
              value={register.email}
              onChange={(e) => setRegister({ ...register, email: e.target.value })}
              id="email" 
              type="email" 
              placeholder="Endereço de email" 
            />
          </InputContainer>

          <InputContainer>
            <label>Senha</label>
            <input 
              value={register.password}
              onChange={(e) => setRegister({ ...register, password: e.target.value })}
              id="password" 
              type="password" 
              placeholder="Digite uma senha" 
            />
          </InputContainer>


          <InputContainer>
            <label>Digite a senha novamente</label>
            <input 
              value={register.password2}
              onChange={(e) => setRegister({ ...register, password2: e.target.value })}
              id="password2" 
              type="password" 
              placeholder="Para confirmar, digite novamente sua senha" />
          </InputContainer>


          <Button 
            onClick={Register}
          >Cadastre-se</Button>

        </Container>
      </ReactModal>
    </>
  );
}
