import { Container } from "./styles";
import { Emoji } from "emoji-mart";

import Options from "../../../assets/options.svg"

type CategoryCardProps = {
  data: {
    emojiID: string;
    content: string;
    bgColor: string;
    textColor: string;  
  }
}

export function CategoryCard(props: CategoryCardProps) {

  return (
    <Container style={{ backgroundColor: props.bgColor }}>
      <header>
        <Emoji emoji={props.emojiID} set='facebook' size={64} />

        <img src={Options} alt="Configurações da categoria" />
      </header>

      <section style={{ color: props.textColor }}>
        {props.content}
      </section>
    </Container>
  );
}
